import React, { useEffect, useState } from "react"
import useAxios from "../../axios/useAxios"
const MasterAddTask = () => {
    const api = useAxios()
    const [students, setStudents] = useState([])
    console.log(students)
    const data = async () => {
        try {
          const response = await api.get(`/students/data/`, {})
          
          setStudents(response.data)
          
        } catch (err) {
          console.log(err)
        }
      }
      useEffect(() => {
        data()
      }, [])
  return (
    <div>
      <div className="grid grid-cols-6 gap-4">
        <div className="col-start-2 col-span-4 text-center border bg-gray-100 p-6 shadow-xl mt-20 rounded-lg">
          <h1 className="text-2xl font-extrabold ">ADD TASK</h1>
          <div className="flex justify-between">
        <div className="relative w-full lg:max-w-sm ml-28 mt-10">
          <select
            className=" p-1 text-gray-700 bg-white border font-semibold rounded-md shadow-sm outline-none appearance-none uppercase"
            // onChange={(event) => {
            //   setBuyerName(event.target.value)
            // }}
            // value={buyer_name}
          >
            <option>SELECT THE STUDENT</option>
            {students?.map((student) => (
              <option> {student.username}</option>
            ))}
          </select>
        </div>
        <div className="relative w-full lg:max-w-sm ml-28 mt-10">
          <select
            className=" p-1 text-gray-700 bg-white border font-semibold rounded-md shadow-sm outline-none appearance-none uppercase"
            // onChange={(event) => {
            //   setBuyerName(event.target.value)
            // }}
            // value={buyer_name}
          >
            <option>SELECT THE OPERATION</option>
            
              <option>ADDITION</option>
              <option>SUBSTRACTION</option>
              <option>MULTIPLICATION</option>
              <option>DIVISION</option>
           
          </select>
        </div>
        <div className="relative w-full lg:max-w-sm ml-28 mt-10">
        <button type="button" class="focus:outline-none text-white bg-black font-medium rounded-lg text-sm px-5 py-1 mt-1 mr-2 mb-2 ">ADD</button>
        </div>

        <div></div>
      </div>
        </div>
      </div>
    </div>
  )
}

export default MasterAddTask
